      alert("User found");
      window.location.href = "/result";
    } else {
      alert("User not found");
    }
  }

  return (
    <div
      className="text-center"
      style={{
        paddingTop: "0",
        paddingLeft: "0",
        paddingRight: "0",
        paddingBottom: "15%",
      }}
    >
      <form onSubmit={consultation}>
        <img
          className="mb-4"
          src={require("../images/logo_offcorss.png")}
          alt="Logo"
        />

        <h1 className="h3 mb-3 fw-normal">
          Consult vouchers and terms of conditions
        </h1>

        <div className="form-floating">
          <input
            type="identification"
            className="form-control"
            id="floatingInputIdentification"
            placeholder="1234567890"
            value={identification}
            onChange={(e) => setIdentification(e.target.value)}
          />

          <label htmlFor="floatingInput">Enter the identity document</label>
        </div>
        <input
          className="w-50 btn btn-lg btn-primary"
          type="submit"
          value="Consultation"
        />
      </form>
    </div>
  );
}

export default App;
