import React, { useState } from "react";
import Modal from "./TermsOfUse";
import "../Styles/Register.css";

function App() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  async function registerUser(event) {
    event.preventDefault();

    const response = await fetch("http://localhost:1337/api/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name,
        email,
        password,
      }),
    });

    const data = await response.json();
    if (data.status === "error") {
      alert("El correo ingresado ya se encuentra registrado");
    } else {
      alert("Registro exitoso");
      window.location.href = "/inicio";
    }

    console.log(data);
  }

  // Para verificar que el check box este seleccionado
  const [isChecked, setIsChecked] = useState(false);

  const handleCheckboxChange = () => {
    setIsChecked(!isChecked);
  };

  return (
    <div
      className="text-center"
      style={{
        paddingTop: "0%",
        paddingLeft: "0%",
        paddingRight: "0%",
        paddingBottom: "15%",
      }}
    >
      <form onSubmit={registerUser}>
        <img
          className="mb-4"
          src={require("../images/logo_offcorss.png")}
          alt="Logo"
        />
        <h1 className="h3 mb-3 fw-normal">Register</h1>
        <div className="form-floating">
          <input
            className="form-control"
            id="floatingInputName"
            placeholder="Name"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <label htmlFor="floatingInputName"> Name </label>
        </div>

        <div className="form-floating">
          <input
            className="form-control"
            id="floatingInputName"
            placeholder="Name"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <label htmlFor="floatingInputName"> Name </label>
        </div>
        <div className="form-floating">
          <input
            type="email"
            className="form-control"
            id="floatingInput"
            placeholder="name@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label htmlFor="floatingInput">Email address</label>
        </div>
        <div className="form-floating">
          <input
            type="password"
            className="form-control"
            id="floatingPassword"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label htmlFor="floatingPassword">Password</label>
        </div>
        <input
          type="checkbox"
          checked={isChecked}
          onChange={handleCheckboxChange}
        />{" "}
        Acepta los términos y condiciones
        {isChecked && <Modal />}
        <div className="checkbox mb-3"></div>
        <input
          className="w-50 btn btn-lg btn-primary"
          type="submit"
          disabled={!isChecked}
          value="Registrarte"
        />
        <p className="mt-5 mb-3 text-muted">© 2023–2023</p>
      </form>
    </div>
  );
}

export default App;
